import { initializeApp } from 'firebase/app';
import { getAuth, GoogleAuthProvider } from 'firebase/auth';
import { getFirestore } from 'firebase/firestore';

const firebaseConfig = {
  apiKey: "AIzaSyB3JuUxs8onaAfdr-rfS5QPjezCs6_cKJA",
  authDomain: "rent-easy-gh.firebaseapp.com",
  projectId: "rent-easy-gh",
  storageBucket: "rent-easy-gh.firebasestorage.app",
  messagingSenderId: "675579381523",
  appId: "1:675579381523:web:52765e3d91e66e5b653ce6",
  measurementId: "G-GX72SBH1VL"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Initialize Firebase Authentication and get a reference to the service
export const auth = getAuth(app);

// Initialize Cloud Firestore and get a reference to the service
export const db = getFirestore(app);

// Google Auth Provider
export const googleProvider = new GoogleAuthProvider();
googleProvider.setCustomParameters({
  prompt: 'select_account',
  hd: '' // Remove any domain restrictions
});

// Add required scopes
googleProvider.addScope('email');
googleProvider.addScope('profile');

export default app;
