import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Home, Heart, MapPin, Star } from 'lucide-react';

interface AppLoaderProps {
  onLoadingComplete: () => void;
}

const AppLoader: React.FC<AppLoaderProps> = ({ onLoadingComplete }) => {
  const [progress, setProgress] = useState(0);
  const [currentMessage, setCurrentMessage] = useState(0);

  const loadingMessages = [
    "Welcome to Ghana's premier rental platform...",
    "Connecting you with verified landlords...",
    "Preparing your perfect home search...",
    "Almost ready to find your dream home!"
  ];

  const inspirationalQuotes = [
    "Home is where your story begins 🏡",
    "Every journey starts with finding the right place 🌟",
    "Your perfect home in Ghana awaits you 🇬🇭",
    "Welcome home to endless possibilities ✨"
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setProgress((prevProgress) => {
        if (prevProgress >= 100) {
          clearInterval(timer);
          setTimeout(() => onLoadingComplete(), 500);
          return 100;
        }
        return prevProgress + 2;
      });
    }, 60);

    return () => clearInterval(timer);
  }, [onLoadingComplete]);

  useEffect(() => {
    const messageTimer = setInterval(() => {
      setCurrentMessage((prev) => (prev + 1) % loadingMessages.length);
    }, 1500);

    return () => clearInterval(messageTimer);
  }, [loadingMessages.length]);

  const floatingVariants = {
    animate: {
      y: [-20, 20, -20],
      rotate: [0, 10, -10, 0],
      transition: {
        duration: 4,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  const pulseVariants = {
    animate: {
      scale: [1, 1.2, 1],
      opacity: [0.7, 1, 0.7],
      transition: {
        duration: 2,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0, scale: 0.9 }}
        transition={{ duration: 0.5 }}
        className="fixed inset-0 z-50 flex items-center justify-center relative overflow-hidden"
        style={{
          backgroundImage: `url('/src/assets/images/home_background.jpg')`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat'
        }}
      >
        {/* Multiple blur layers for beautiful depth effect */}
        <div className="absolute inset-0 bg-black/20"></div>
        <div className="absolute inset-0 backdrop-blur-md"></div>
        <div className="absolute inset-0 bg-gradient-to-br from-primary-900/40 via-primary-800/30 to-secondary-900/40"></div>
        <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-transparent to-black/30"></div>
        
        {/* Additional soft overlay for better text readability */}
        <div className="absolute inset-0 bg-white/10 backdrop-blur-sm"></div>

        {/* Background Decorative Elements - now more subtle */}
        <div className="absolute inset-0 overflow-hidden opacity-30">
          <motion.div
            variants={floatingVariants}
            animate="animate"
            className="absolute top-20 left-20 w-32 h-32 rounded-full bg-gradient-to-br from-white/20 to-primary-300/30 backdrop-blur-sm"
          />
          <motion.div
            variants={floatingVariants}
            animate="animate"
            style={{ animationDelay: '1s' }}
            className="absolute top-40 right-32 w-24 h-24 rounded-full bg-gradient-to-br from-white/20 to-secondary-300/30 backdrop-blur-sm"
          />
          <motion.div
            variants={floatingVariants}
            animate="animate"
            style={{ animationDelay: '2s' }}
            className="absolute bottom-32 left-1/3 w-20 h-20 rounded-full bg-gradient-to-br from-white/20 to-accent-300/30 backdrop-blur-sm"
          />
          <motion.div
            variants={floatingVariants}
            animate="animate"
            style={{ animationDelay: '0.5s' }}
            className="absolute bottom-20 right-20 w-16 h-16 rounded-full bg-gradient-to-br from-white/20 to-primary-300/30 backdrop-blur-sm"
          />
        </div>

        <div className="relative z-10 text-center max-w-md mx-auto px-6">
          {/* Logo Animation */}
          <motion.div
            initial={{ scale: 0.5, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
            className="mb-8"
          >
            <div className="relative inline-block">
              <motion.div
                variants={pulseVariants}
                animate="animate"
                className="w-24 h-24 bg-gradient-to-br from-white/90 to-primary-100 backdrop-blur-md rounded-2xl flex items-center justify-center text-primary-600 shadow-2xl mx-auto border border-white/20"
              >
                <Home className="w-12 h-12" />
              </motion.div>
              <motion.div
                animate={{ rotate: 360 }}
                transition={{ duration: 8, repeat: Infinity, ease: "linear" }}
                className="absolute -top-2 -right-2 w-8 h-8 bg-gradient-to-br from-accent-400 to-accent-500 rounded-full flex items-center justify-center shadow-lg"
              >
                <Star className="w-4 h-4 text-white" />
              </motion.div>
            </div>
          </motion.div>

          {/* Brand Name */}
          <motion.h1
            initial={{ y: 30, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.3 }}
            className="text-4xl font-display font-bold text-white mb-4 drop-shadow-lg"
          >
            RentEasy GH
          </motion.h1>

          {/* Inspirational Quote */}
          <motion.div
            key={currentMessage}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
            className="mb-8"
          >
            <p className="text-lg text-white font-medium mb-2 drop-shadow-md">
              {inspirationalQuotes[currentMessage]}
            </p>
            <div className="flex items-center justify-center space-x-2 text-white/90">
              <MapPin className="w-4 h-4" />
              <span className="text-sm drop-shadow-sm">{loadingMessages[currentMessage]}</span>
            </div>
          </motion.div>

          {/* Progress Bar */}
          <div className="mb-6">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-medium text-white/90 drop-shadow-sm">Loading</span>
              <span className="text-sm font-medium text-white drop-shadow-sm">{Math.round(progress)}%</span>
            </div>
            <div className="w-full bg-white/20 backdrop-blur-sm rounded-full h-3 overflow-hidden shadow-inner border border-white/30">
              <motion.div
                initial={{ width: 0 }}
                animate={{ width: `${progress}%` }}
                transition={{ duration: 0.3, ease: "easeOut" }}
                className="h-full bg-gradient-to-r from-white via-primary-200 to-secondary-300 rounded-full relative overflow-hidden shadow-lg"
              >
                {/* Shimmer Effect */}
                <motion.div
                  animate={{ x: [-100, 400] }}
                  transition={{ duration: 1.5, repeat: Infinity, ease: "linear" }}
                  className="absolute top-0 left-0 h-full w-20 bg-gradient-to-r from-transparent via-white/50 to-transparent transform skew-x-12"
                />
              </motion.div>
            </div>
          </div>

          {/* Loading Icons */}
          <div className="flex items-center justify-center space-x-6">
            {[Home, Heart, MapPin, Star].map((Icon, index) => (
              <motion.div
                key={index}
                initial={{ scale: 0.8, opacity: 0.5 }}
                animate={{ 
                  scale: progress > (index + 1) * 25 ? 1.2 : 0.8,
                  opacity: progress > (index + 1) * 25 ? 1 : 0.6
                }}
                transition={{ duration: 0.3 }}
                className={`w-8 h-8 rounded-full flex items-center justify-center backdrop-blur-sm border ${
                  progress > (index + 1) * 25 
                    ? 'bg-white text-primary-600 shadow-lg border-white/30' 
                    : 'bg-white/20 text-white/70 border-white/20'
                }`}
              >
                <Icon className="w-4 h-4" />
              </motion.div>
            ))}
          </div>

          {/* Footer Text */}
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8, delay: 1 }}
            className="text-xs text-white/80 mt-8 drop-shadow-sm"
          >
            Preparing your amazing rental experience...
          </motion.p>
        </div>
      </motion.div>
    </AnimatePresence>
  );
};

export default AppLoader;
